  //component create

  function submitNote(event){
    props.onAdd(note);
    setNote({
      title:"",
      content:""
    })
    const createUser = () => {
      Axios.post("http://localhost:3001/createUser", { title, content }).then(
        (response) => {
          alert("Created!");
          setListOfUsers([...listOfUsers, { title: title, content: content }]);
        }
      );
    };
    event.preventDefault(); //prevents reloading on add

  }



  return (
    <div>
      <form>
        <input name="title" onChange={(event) => {
            //cr
            setTitle(event.target.value);
          }} value={note.title} placeholder="Title" />
        <textarea name="content" onChange={(event) => {
            //cr
            setContent(event.target.value);
          }} value={note.content} placeholder="Take a note..." rows="3" />
        <button onClick={createUser}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
