import React, { useState, useEffect } from "react";
import Header from "./Header";
import Footer from "./Footer";
// import Note from "./Note";
import CreateArea from "./CreateArea";
import Axios from "axios";
// import { deleteOne } from "../../../server/users";


function App() {

  //component
  // const [notes,setNotes]=useState([])

  const [listOfUsers, setListOfUsers] = useState([]); //conponent app

  useEffect(() => {
    //api call
    const interval = setInterval(() => {
      Axios.get("http://localhost:3001/getUsers").then((response) => {
        setListOfUsers(response.data);
      });
    }, 500)

  }, []);


  function addNote() {
    // setNotes(prevNotes=>{
    //   return[...prevNotes,newNote]
    // })

    Axios.post("http://localhost:3001/createUser", { title, content }).then(
      (response) => {
        setListOfUsers([...listOfUsers, { title, content }]);
      }
    );


  }


  const getData=()=>{
    Axios.get("http://localhost:3001/getUsers").then((response) => {
      setListOfUsers(response.data);
    });
  }

  const onDelete=(id)=>{
    Axios.delete(`http://localhost:3001/deleteUser/${id}`).then(()=>{
      getData();
    }) 
  }

  // function deleteNote() {
  // setNotes(prevNotes=>{
  //   return prevNotes.filter((noteItem,index) => {
  //     return index!==id;
  //   });
  // })

  // return 
  const deleteNote =async (id) => {
   await Axios.delete(`http://localhost:3001/deleteUser/:${id}`)
    .then(() => {
      setListOfUsers(
        listOfUsers.filter((val) => {
          return val._id!= id;
        })
      )
    })
  };



  // .then(()=>{
  //   setListOfUsers(
  //     listOfUsers.filter((noteItem,index)=>{
  //       return index!=id;
  //     })
  //   )
  // })
  // .then(()=>{

  // }
  // )






  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      {listOfUsers.map((user) => {
        return ( <div className="note">
        <h1>{user.title}</h1>
        <p>{user.content}</p>
        <button onClick={()=>{deleteNote(user.id)}}>DELETE</button>
      </div>);

        // <Note key={index} id={index} title={user.title} content={user.content} onDelete={deleteNote(user._id)} />

      })}
      <Footer />
    </div>
  );
}

export default App;
