import React,{useState,useEffect} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import Axios from "axios";


function App() {

  //component
  // const [notes,setNotes]=useState([])

  const [listOfUsers, setListOfUsers] = useState([]); //conponent app



  function addNote(){
    // setNotes(prevNotes=>{
    //   return[...prevNotes,newNote]
    // })
    useEffect(() => {
      //api call
      Axios.get("http://localhost:3001/getUsers").then((response) => {
        setListOfUsers(response.data);
      });
    }, []);
    Axios.post("http://localhost:3001/createUser", { title, content }).then(
      (response) => {
        setListOfUsers([...listOfUsers, { title: title, content: content }]);
      }
    );

  }

  function deleteNote(id){
    setNotes(prevNotes=>{
      return prevNotes.filter((noteItem,index) => {
        return index!==id;
      });
    })
  }


  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote}/>
      {listOfUsers.map((user,index)=>{
        return <Note key={index} id={index} title={user.title} content={user.content} onDelete={deleteNote} />
      })}
      <Footer />
    </div>
  );
}

export default App;
